function createPixel(x, y, color,) {
    const pixel = document.createElement('div');
    pixel.style.width = '7px';
    pixel.style.height = '7px';
    pixel.style.backgroundColor = color;
    pixel.style.position = 'absolute';
    pixel.style.left = `${x * 7}px`;
    pixel.style.top = `${y * 7}px`;
    myDiv.appendChild(pixel);
}

function visualizeLogo() {
    
    browser.storage.local.get(['leLogo', 'x_0', 'y_0', 'width', 'height', 'message', 'version']).then((result) => {
        const leLogo = result.leLogo;
        const x_0 = result.x_0;
        const y_0 = result.y_0;
        const width = result.width;
        const height = result.height;
        const message = result.message;
        const version = result.version;

        console.log('Data fetched:', leLogo, x_0, y_0, width, height, message, version);

        p = document.getElementById('logoText');
        p.innerHTML = message;
    
        for (let y = 0; y < height; y++) {
            for (let x = 0; x < width; x++) {
                const index = x + y * width;
                const colorIndex = leLogo[index];
                const color = idMapping[colorIndex];
                createPixel(x, y, color);
            }
        }

    }).catch((error) => {
        console.error('Error fetching data:', error);
    });

}

